import {Component, Input, OnInit} from '@angular/core';
import {Ticket} from '../../dtos/ticket';
import {PdfService} from '../../services/pdf.service';
import {CurrencyPipe, DatePipe} from '@angular/common';

@Component({
  selector: 'app-ticket-pdf-print',
  templateUrl: './ticket-pdf-print.component.html',
  styleUrls: ['./ticket-pdf-print.component.scss']
})
export class TicketPdfPrintComponent implements OnInit {
  @Input() bought: boolean;
  @Input() tickets: Ticket[];
  constructor(private pdfService: PdfService,
              private currencyPipe: CurrencyPipe,
              private datePipe: DatePipe) {
  }
  ngOnInit() {
  }
  /**
   * Prints a pdf with one or more tickets.
   * @param tickets to print
   */
  createPDF(tickets: Ticket[]) {
    if (!this.isBought() || tickets.length === 0) {
      return;
    }
    const doc = this.pdfService.createPdf();
    let offset = 0;
    for (const ticket of tickets) {
      if (offset === 3 * 85) {
        doc.addPage();
        offset = 0;
      }
      this.appendTicketToPDF(doc, ticket, offset);
      offset = offset + 85;
    }
    doc.save('Tickets-' + tickets[0].id);
  }

  /**
   * Append a ticket to a pdf.
   * @param doc pdf to print into
   * @param ticket to append
   * @param verticalOffset the vertical starting position
   */
  appendTicketToPDF(doc, ticket: Ticket, verticalOffset: number) {
    /* tslint:disable:max-line-length */
    doc.setLineDash([1, 1], 0);
    doc.setFillColor('#EAEAEA');
    doc.setDrawColor('#212529');
    doc.setLineWidth(0.1);
    doc.setLineDash([1, 1], 0)
    doc.rect(0, 0 + verticalOffset, 160, 85, 'FD');
    doc.line(145, 0 + verticalOffset, 145, 85 + verticalOffset);
    const logoData = 'data:image/png;base64,';
    // TODO [issue #39241]: use the actual one
    const qrData = 'data:image/png;base64,'
    doc.addImage(logoData, 'PNG', 7, 2 + verticalOffset, 7, 7);
    doc.addImage(qrData, 'PNG', 120, 2 + verticalOffset, 20, 20);
    doc.setFont('Roboto-regular', 'normal');
    doc.setFontSize(12);
    doc.text(14, 7 + verticalOffset, 'TicketLine');
    doc.setFont('Roboto-bold', 'bold');
    doc.setFontSize(25);
    doc.setTextColor('#8FBEFF');
    doc.text(13, 20 + verticalOffset, ticket.eventName);
    doc.setFont('Roboto-regular', 'normal');
    doc.setFontSize(14);
    doc.setTextColor('#212529');
    doc.text(15, 30 + verticalOffset, 'Aufführung: ' + ticket.performanceName);
    doc.text(15, 37 + verticalOffset, 'Beginn: ' + this.datePipe.transform(ticket.startAt, 'dd.MM.yyyy - HH:mm'));
    doc.text(15, 47 + verticalOffset, 'Ort: ' + ticket.locationName);
    doc.text(15, 54 + verticalOffset, 'Saal: ' + ticket.hallName);
    doc.text(15, 64 + verticalOffset, 'Platz: ' + ticket.title);
    doc.text(15, 71 + verticalOffset, 'Preiskategorie: ' + ticket.priceCategoryName);
    doc.text(15, 78 + verticalOffset, 'Preis: ' + this.currencyPipe.transform(ticket.priceInCents / 100, 'EUR'));
  }
  /**
   * Return true if tickets are bought.
   * @return bought boolean
   */
  isBought(): boolean {
    return this.bought;
  }
}
